export function getNav(baseHref) {
    const hub = (path) => {
        if (!baseHref)
            return path;
        // baseHref like "https://www.drwan.com"
        return baseHref.replace(/\/$/, "") + path;
    };
    return [
        { key: "tools", labelEn: "Tools", labelZh: "工具", href: hub("/#tools") },
        { key: "maps", labelEn: "Maps", labelZh: "图谱", href: hub("/#maps") },
        { key: "knowledge", labelEn: "Knowledge", labelZh: "知识", href: hub("/#knowledge") },
        { key: "herbs", labelEn: "Herbs", labelZh: "本草", href: hub("/#herbs") },
        { key: "learn", labelEn: "Learn", labelZh: "学习", href: hub("/#learn") },
        { key: "games", labelEn: "Games", labelZh: "小游戏", href: hub("/#games") },
        { key: "news", labelEn: "News", labelZh: "新闻", href: hub("/news") },
    ];
}
